import React from 'react';

class AddMovie extends React.Component {
  constructor(props) {
    super(props);
    this.state = { title: '', subtitle: '', storyline: '', imagePath:'' };
  }

  render() {
    const { title, subtitle, storyline } = this.state;
    return (
      <form>
        <label htmlFor="title">
          <p>Título</p>
          <input
            type="text"
            id="title"
            name="title"
            value={title}
            onChange={(event) => this.setState({ title: event.target.value })}
          />
        </label>

        <label htmlFor="subtitle">
          <p>Subitítulo</p>
          <input
            type="text"
            id="subtitle"
            name="subtitle"
            value={subtitle}
            onChange={(event) => this.setState({ subtitle: event.target.value })}
          />
        </label>

        <label htmlFor="imagem">
        <input type="file" 
        name="image" 
        value={imagePath}
        />
        </label>


        <label htmlFor="storyline">
          <p>Sinopse</p>
          <textarea
            type="text"
            id="storyline"
            name="storyline"
            value={storyline}
            onChange={(event) => this.setState({ storyline: event.target.value })}
          />
        </label>

      </form>
    );
  }
}

export default AddMovie;
